function AddReadMoreForShort(){$(".addReadMore.short").each(function(){if(!$(this).find(".firstSec").length){var e=$(this).text();if(e.length>60){var s=e.substring(0,60),a=e.substring(60,e.length);$(this).html(s+"<a class='SecSec'>"+a+"</a><a class='readMore f-p'>...Read More</a><a class='readLess f-p'> Read Less</a>")}}}),$(document).on("click",".readMore,.readLess",function(){$(this).closest(".addReadMore.short").toggleClass("showlesscontent showmorecontent")})}function AddReadMoreForLong(){$(".addReadMore.long").each(function(){if(!$(this).find(".firstSec").length){var e=$(this).text();if(e.length>200){var s=e.substring(0,200),a=e.substring(200,e.length);$(this).html(s+"<a class='SecSec'>"+a+"</a><a class='readMore f-p'>...Read More</a><a class='readLess f-p'> Read Less</a>")}}}),$(document).on("click",".readMore,.readLess",function(){$(this).closest(".addReadMore.long").toggleClass("showlesscontent showmorecontent")})}AddReadMoreForLong(),AddReadMoreForShort();